var jsf = require('./lib');

var s = {
  "properties":{
    "hexString":{
      "type":"string"
    },
    "rgbArray": {
      "type":"array"
    }
  },
  "oneOf" :[{
    "required": ["hexString"]
  },{
    "required": ["rgbArray"]
  }]
};

var s = {
  "id": "User",
  "type": "object",
  "properties": {
    "num": {
      "type": "number",
      "minimum": 0,
      "maximum": 1,
      "multipleOf": 0.01
    }
  },
  "required": ["num"]
};

jsf(s).then((result) => {
  console.log(typeof result);
  console.log(JSON.stringify(result, null, 2));
}).catch((error) => {
  console.log(error.stack);
});
